mapper.select:
select count(*) from expert_information


        name like CONCAT('%',#{name},'%')


        and nature_work = #{natureWork}


        and professional_field = #{professionalField}


        and is_province = #{isProvince}


        and is_state in(1,2) and is_delete = 1

SELECT id, name, sex, birth_date, education, present_post, technical_title, study_major, professionalism, work_unit, postal_address, postal_code, work_telephone, phone, mail, nature_work, professional_field, curriculum_vitae, recommendation_unit_opinion, is_state, is_province, expert_information_url_id from expert_information
name like CONCAT('%',#{name},'%')
and nature_work = #{natureWork}
and professional_field = #{professionalField}
and is_province = #{isProvince}
and is_state in(1,2) and is_delete = 1
